// select2 
function select2ExpandGroup(data) {
    for (var i = 0; i < data.length; i++) {
        if (data[i].id.indexOf('group_') === 0 && data[i].users) {
            var group = data.splice(i, 1)[0];
            for (var j = 0; j < group.users.length; j++) {
                data.push(group.users[j]);
            }
        }
    }
    return data;
};

